org.jenkinsci.plugins.managedscripts.Scr.content:
declare kemumaki_path=${KEMUMAKI_DIR}
[[ -d "${kemumaki_path}" ]] || git clone https://github.com/axsh/kemumaki.git ${kemumaki_path}

cd ${kemumaki_path}

cat <<END
###################################################
# Check for git merge failure of branch -> master
###################################################
END

declare revision=$(git ls-remote https://github.com/axsh/kemumaki.git | egrep "[0-9a-f]{40}[[:space:]]+refs/.*?/master" | cut -c1-40)
git fetch origin && git fetch --tags origin && git reset --hard ${revision}

git submodule update --init --recursive
